import React from 'react';
import './PlayersContainer.css';
import fakeData from '../../fakeData';
import { useState } from 'react';
import PlayerCard from '../PlayerCard/PlayerCard';

const PlayersContainer = () => {
    const firstTenPlayer = fakeData.slice(0, 10);
    const [players, setPlayers] = useState(firstTenPlayer)
    return (
        <div className="players-container d-flex">
            <div className="single-player-container">
                {
                    players.map(playerInfo => <PlayerCard player={playerInfo} key={playerInfo.id}></PlayerCard>)
                }
            </div>
            <div className="team-information-viwer justify-content-center">
                <div className="team-info text-center justify-content-center">
                    <div className="d-flex text-center justify-content-center">
                        <h4><small>Team name:</small> <span style
                        ={{ color: "red" }}>PSYs0s's 11</span></h4>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default PlayersContainer;
